import { useState } from 'react'
import { Slider } from "@/components/ui/slider"; 
import { Label } from "@/components/ui/label";

const Command = () => {

  const [ledState, setLedState] = useState('OFF');
  const [angle, setAngle] = useState(0);

  const changeState = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/led");
      const data = await response.json();

      console.log(data);

      setLedState(data.ledState);
    } catch (error) {
      setLedState("ERROR");
      console.error("Error fetching message:", error);
    }
  };


  return (
    <div className="commands">
      <h1>COMMANDS</h1>

      <br></br>

      <p> {ledState} </p>

      <br></br>

      <button className="buttonS1" onClick={changeState}> CHANGE LED </button>


      <br></br><br></br><br></br>


      <Label htmlFor="angle-slider" className="text-lg font-medium">
        Angle: {angle}Â°
      </Label>
      <Slider
        id="angle-slider"
        min={-180}
        max={180}
        step={1}
        value={[angle]}
        onValueChange={(val) => setAngle(val[0])}
        className="w-full"
      />

    </div>




  );
}

export default Command;
